import React, { ReactNode, useEffect, useState, useCallback } from "react";
import {
  AnalyticsProvider as SegmentAnalyticsProvider,
  SegmentClient,
} from "@segment/analytics-react-native";
import { start } from "./segment";
import type { AppStore } from "../reducers";

const AnalyticsProvider = ({
  store,
  children,
}: {
  store: AppStore;
  children?: ReactNode;
}): JSX.Element | null => {
  const [loaded, setLoaded] = useState<boolean>(false);
  const [segmentClient, setSegmentClient] = useState<
    SegmentClient | undefined
  >();

  const loadSegment = useCallback(async (store: AppStore) => {
    try {
      const result: SegmentClient | undefined = await start(store);
      setSegmentClient(result);
    } catch (error) {
      console.error(`Failed to initialize Segment with error: ${error}`);
    }
    setLoaded(true);
  }, []);

  useEffect(() => {
    loadSegment(store);
  }, []);

  if (!loaded || !segmentClient) {
    return <>{children}</>;
  }

  return (
    <SegmentAnalyticsProvider client={segmentClient}>
      {children}
    </SegmentAnalyticsProvider>
  );
};

export default AnalyticsProvider;

import { useEffect, memo } from "react";
// eslint-disable-next-line import/no-cycle
import { track } from "./segment";

type Props = {
  onMount?: boolean;
  onUnmount?: boolean;
  onUpdate?: boolean;
  event: string;
  mandatory?: boolean;
  [key: string]: unknown;
};

export default memo((props: Props): null => {
  const { event, onMount, onUnmount, onUpdate } = props;

  function doTrack() {
    const { event, onMount, onUnmount, onUpdate, mandatory, ...properties } =
      props;
    track(event, properties, mandatory);
  }

  useEffect(
    function mount() {
      if (typeof event !== "string") {
        console.warn("analytics Track: invalid event=", event);
      }

      if (onMount) doTrack();
      return function unmount() {
        if (onUnmount) doTrack();
      };
    },
    // eslint-disable-next-line react-hooks/exhaustive-deps
    [],
  );

  if (onUpdate) {
    doTrack();
  }

  return null;
});

import { useEffect, useRef } from "react";
import { useIsFocused } from "@react-navigation/native";
// eslint-disable-next-line import/no-cycle
import { screen } from "./segment";

import { previousRouteNameRef, currentRouteNameRef } from "./screenRefs";

type Props = {
  [key: string]: unknown;
} & {
  category?: string;
  name?: string;
  refreshSource?: boolean;
};

export default function TrackScreen({
  category,
  name,
  refreshSource = true,
  ...props
}: Props) {
  const isFocused = useIsFocused();
  const isFocusedRef = useRef<boolean>();

  // Analytics tracking
  useEffect(() => {
    if (isFocusedRef.current !== isFocused) {
      isFocusedRef.current = isFocused;
      if (isFocusedRef.current) {
        if (refreshSource) {
          previousRouteNameRef.current = currentRouteNameRef.current;
          currentRouteNameRef.current = `${
            category + (name ? ` ${name}` : "")
          }`;
        } else {
          previousRouteNameRef.current = currentRouteNameRef.current;
        }
        screen(category, name, props);
      }
    }
  }, [category, name, props, isFocused, refreshSource]);

  return null;
}

/* eslint-disable no-console */
import "crypto";
import { v4 as uuid } from "uuid";
import * as Sentry from "@sentry/react-native";
import Config from "react-native-config";
import { Platform } from "react-native";
import { createClient, SegmentClient } from "@segment/analytics-react-native";
import VersionNumber from "react-native-version-number";
import RNLocalize from "react-native-localize";
import { ReplaySubject } from "rxjs";
import {
  getFocusedRouteNameFromRoute,
  ParamListBase,
  RouteProp,
  useRoute,
} from "@react-navigation/native";
import { snakeCase } from "lodash";
import { useCallback } from "react";
import { idsToLanguage } from "@ledgerhq/types-live";
import {
  getAndroidArchitecture,
  getAndroidVersionCode,
} from "../logic/cleanBuildVersion";
import getOrCreateUser from "../user";
// eslint-disable-next-line import/no-cycle
import {
  analyticsEnabledSelector,
  languageSelector,
  localeSelector,
  lastSeenDeviceSelector,
  sensitiveAnalyticsSelector,
  firstConnectionHasDeviceSelector,
  firstConnectHasDeviceUpdatedSelector,
  readOnlyModeEnabledSelector,
  hasOrderedNanoSelector,
  notificationsSelector,
} from "../reducers/settings";
import { knownDevicesSelector } from "../reducers/ble";
import { DeviceLike, State } from "../reducers/types";
import { satisfactionSelector } from "../reducers/ratings";
import { accountsSelector } from "../reducers/accounts";
import type { AppStore } from "../reducers";
import { NavigatorName } from "../const";
import { previousRouteNameRef, currentRouteNameRef } from "./screenRefs";
import { AnonymousIpPlugin } from "./AnonymousIpPlugin";
import { UserIdPlugin } from "./UserIdPlugin";
import { Maybe } from "../types/helpers";
import {
  GENESIS_PASS_COLLECTION_CONTRACT,
  INFINITY_PASS_COLLECTION_CONTRACT,
  hasNftInAccounts,
} from "../helpers/nfts";
import { appStartupTime } from "../StartupTimeMarker";

let sessionId = uuid();
const appVersion = `${VersionNumber.appVersion || ""} (${
  VersionNumber.buildVersion || ""
})`;
const { ANALYTICS_LOGS, ANALYTICS_TOKEN } = Config;

export const updateSessionId = () => (sessionId = uuid());

const extraProperties = async (store: AppStore) => {
  const state: State = store.getState();
  const sensitiveAnalytics = sensitiveAnalyticsSelector(state);
  const systemLanguage = sensitiveAnalytics
    ? null
    : RNLocalize.getLocales()[0]?.languageTag;
  const language = sensitiveAnalytics ? null : languageSelector(state);
  const region = sensitiveAnalytics ? null : localeSelector(state);
  const devices = knownDevicesSelector(state);
  const satisfaction = satisfactionSelector(state);
  const accounts = accountsSelector(state);
  const lastDevice =
    lastSeenDeviceSelector(state) || devices[devices.length - 1];
  const deviceInfo = lastDevice
    ? {
        deviceVersion: lastDevice.deviceInfo?.version,
        deviceLanguage:
          lastDevice.deviceInfo?.languageId !== undefined
            ? idsToLanguage[lastDevice.deviceInfo.languageId]
            : undefined,
        appLength: (lastDevice as DeviceLike)?.appsInstalled,
        modelId: lastDevice.modelId,
      }
    : {};
  const firstConnectionHasDevice = firstConnectionHasDeviceSelector(state);
  const notifications = notificationsSelector(state);
  const notificationsAllowed = notifications.areNotificationsAllowed;
  const notificationsBlacklisted = Object.entries(notifications)
    .filter(
      ([key, value]) => key !== "areNotificationsAllowed" && value === false,
    )
    .map(([key]) => key);
  const firstConnectHasDeviceUpdated =
    firstConnectHasDeviceUpdatedSelector(state);
  const { user } = await getOrCreateUser();
  const accountsWithFunds = accounts
    ? [
        ...new Set(
          accounts
            .filter(account => account?.balance.isGreaterThan(0))
            .map(account => account?.currency?.ticker),
        ),
      ]
    : [];
  const blockchainsWithNftsOwned = accounts
    ? [
        ...new Set(
          accounts
            .filter(account => account.nfts?.length)
            .map(account => account.currency.ticker),
        ),
      ]
    : [];
  const hasGenesisPass = hasNftInAccounts(
    GENESIS_PASS_COLLECTION_CONTRACT,
    accounts,
  );
  const hasInfinityPass = hasNftInAccounts(
    INFINITY_PASS_COLLECTION_CONTRACT,
    accounts,
  );

  return {
    appVersion,
    androidVersionCode: getAndroidVersionCode(VersionNumber.buildVersion),
    androidArchitecture: getAndroidArchitecture(VersionNumber.buildVersion),
    environment: ANALYTICS_LOGS ? "development" : "production",
    systemLanguage: sensitiveAnalytics ? null : systemLanguage,
    language,
    appLanguage: language, // In Braze it can't be called language
    region: region?.split("-")[1] || region,
    platformOS: Platform.OS,
    platformVersion: Platform.Version,
    sessionId,
    devicesCount: devices.length,
    firstConnectionHasDevice,
    firstConnectHasDeviceUpdated,
    ...(satisfaction
      ? {
          satisfaction,
        }
      : {}),
    ...deviceInfo,
    notificationsAllowed,
    notificationsBlacklisted,
    userId: user?.id,
    accountsWithFunds,
    blockchainsWithNftsOwned,
    hasGenesisPass,
    hasInfinityPass,
    appTimeToInteractiveMilliseconds: appStartupTime,
  };
};

type MaybeAppStore = Maybe<AppStore>;

let storeInstance: MaybeAppStore; // is the redux store. it's also used as a flag to know if analytics is on or off.
let segmentClient: SegmentClient | undefined;

const token = ANALYTICS_TOKEN;
export const start = async (
  store: AppStore,
): Promise<SegmentClient | undefined> => {
  const { user, created } = await getOrCreateUser();
  storeInstance = store;

  if (created && ANALYTICS_LOGS) {
    console.log("analytics:identify", user.id);
  }

  console.log("START ANALYTICS", ANALYTICS_LOGS);
  const userExtraProperties = await extraProperties(store);
  if (token) {
    segmentClient = createClient({
      writeKey: token,
      debug: !!ANALYTICS_LOGS,
    });
    // This allows us to not retrieve users ip addresses for privacy reasons
    segmentClient.add({ plugin: new AnonymousIpPlugin() });
    // This allows us to make sure we are adding the userId to the event
    segmentClient.add({ plugin: new UserIdPlugin() });

    if (created) {
      segmentClient.reset();
    }
    await segmentClient.identify(user.id, userExtraProperties);
  }
  await track("Start", userExtraProperties, true);

  return segmentClient;
};
export const updateIdentify = async () => {
  Sentry.addBreadcrumb({
    category: "identify",
    level: "debug",
  });

  if (!storeInstance || !analyticsEnabledSelector(storeInstance.getState())) {
    return;
  }

  const userExtraProperties = await extraProperties(storeInstance);
  if (ANALYTICS_LOGS) console.log("analytics:identify", userExtraProperties);
  if (!token) return;
  await segmentClient?.identify(
    userExtraProperties.userId,
    userExtraProperties,
  );
};
export const stop = () => {
  if (ANALYTICS_LOGS) console.log("analytics:stop");
  storeInstance = null;
};

type Properties = Error | Record<string, unknown> | null;
export type LoggableEvent = {
  eventName: string;
  eventProperties?: Properties;
  eventPropertiesWithoutExtra?: Properties;
  date: Date;
};
export const trackSubject = new ReplaySubject<LoggableEvent>(30);

type EventType = string | "button_clicked" | "error_message";

export function getIsTracking(
  state: State | null | undefined,
  mandatory?: boolean | null | undefined,
): { enabled: true } | { enabled: false; reason?: string } {
  if (!state) return { enabled: false, reason: "store not initialised" };
  const readOnlyMode = state && readOnlyModeEnabledSelector(state);
  const hasOrderedNano = state && hasOrderedNanoSelector(state);
  const analyticsEnabled = state && analyticsEnabledSelector(state);
  if (readOnlyMode && hasOrderedNano)
    return {
      enabled: false,
      reason:
        "not tracking anything in the reborn state post purchase pre device setup",
    };
  if (!mandatory && !analyticsEnabled) {
    return {
      enabled: false,
      reason: "analytics not enabled",
    };
  }
  return { enabled: true };
}

export const track = async (
  event: EventType,
  eventProperties?: Error | Record<string, unknown> | null,
  mandatory?: boolean | null,
) => {
  Sentry.addBreadcrumb({
    message: event,
    category: "track",
    data: eventProperties || undefined,
    level: "debug",
  });

  const state = storeInstance && storeInstance.getState();

  const isTracking = getIsTracking(state, mandatory);
  if (!isTracking.enabled) {
    if (ANALYTICS_LOGS)
      console.log("analytics:track: not tracking because: ", isTracking.reason);
    return;
  }

  const screen = currentRouteNameRef.current;

  const userExtraProperties = await extraProperties(storeInstance as AppStore);
  const propertiesWithoutExtra = {
    screen,
    ...eventProperties,
  };
  const allProperties = {
    ...propertiesWithoutExtra,
    ...userExtraProperties,
  };
  if (ANALYTICS_LOGS) console.log("analytics:track", event, allProperties);
  trackSubject.next({
    eventName: event,
    eventProperties: allProperties,
    eventPropertiesWithoutExtra: propertiesWithoutExtra,
    date: new Date(),
  });
  if (!token) return;
  segmentClient?.track(event, allProperties);
};
export const getPageNameFromRoute = (route: RouteProp<ParamListBase>) => {
  const routeName =
    getFocusedRouteNameFromRoute(route) || NavigatorName.Portfolio;
  return snakeCase(routeName);
};
export const trackWithRoute = (
  event: EventType,
  route: RouteProp<ParamListBase>,
  properties?: Record<string, unknown> | null,
  mandatory?: boolean | null,
) => {
  const newProperties = {
    page: getPageNameFromRoute(route),
    // don't override page if it's already set
    ...(properties || {}),
  };
  track(event, newProperties, mandatory);
};
export const useTrack = () => {
  const route = useRoute();
  const track = useCallback(
    (
      event: EventType,
      properties?: Record<string, unknown> | null,
      mandatory?: boolean | null,
    ) => trackWithRoute(event, route, properties, mandatory),
    [route],
  );
  return track;
};
export const usePageNameFromRoute = () => {
  const route = useRoute();
  return getPageNameFromRoute(route);
};
export const useAnalytics = () => {
  const track = useTrack();
  const page = usePageNameFromRoute();
  return {
    track,
    page,
  };
};
export const screen = async (
  category?: string,
  name?: string | null,
  properties?: Record<string, unknown> | null | undefined,
) => {
  const title = `Page ${category + (name ? ` ${name}` : "")}`;
  Sentry.addBreadcrumb({
    message: title,
    category: "screen",
    data: properties || {},
    level: "info",
  });

  const state = storeInstance && storeInstance.getState();

  const isTracking = getIsTracking(state);
  if (!isTracking.enabled) {
    if (ANALYTICS_LOGS)
      console.log(
        "analytics:screen: not tracking because: ",
        isTracking.reason,
      );
    return;
  }

  const source = previousRouteNameRef.current;

  const userExtraProperties = await extraProperties(storeInstance as AppStore);
  const eventPropertiesWithoutExtra = {
    source,
    ...properties,
  };
  const allProperties = {
    ...eventPropertiesWithoutExtra,
    ...userExtraProperties,
  };
  if (ANALYTICS_LOGS)
    console.log("analytics:screen", category, name, allProperties);
  trackSubject.next({
    eventName: title,
    eventProperties: allProperties,
    eventPropertiesWithoutExtra,
    date: new Date(),
  });
  if (!token) return;
  segmentClient?.track(title, allProperties);
};

import React, { useCallback, useEffect, useMemo, useState } from "react";
import { ScrollView, Linking, TouchableOpacity } from "react-native";
import { Flex, Text } from "@ledgerhq/native-ui";
import { ArrowLeftMedium } from "@ledgerhq/native-ui/assets/icons";
import { useBleDevicesScanning } from "@ledgerhq/live-common/ble/hooks/useBleDevicesScanning";
import { HwTransportErrorType } from "@ledgerhq/errors";
import { useTranslation } from "react-i18next";
import { useSelector } from "react-redux";
import { getDeviceModel } from "@ledgerhq/devices";
import { Device, DeviceModelId } from "@ledgerhq/types-devices";
import { useNavigation } from "@react-navigation/native";
import TransportBLE from "../../react-native-hw-transport-ble";
import { knownDevicesSelector } from "../../reducers/ble";
import LocationRequired from "../LocationRequired/index";
import Animation from "../Animation";
import BleDeviceItem from "./BleDeviceItem";
import lottie from "./assets/bluetooth.json";
import { urls } from "../../config/urls";
import { TrackScreen, track } from "../../analytics";
import { useResetOnNavigationFocusState } from "../../helpers/useResetOnNavigationFocusState";

export type FilterByDeviceModelId = null | DeviceModelId;
const CANT_SEE_DEVICE_TIMEOUT = 5000;

export type BleDevicesScanningProps = {
  onDeviceSelect: (item: Device) => void;
  onGoBack?: () => void;
  filterByDeviceModelId?: FilterByDeviceModelId;
  areKnownDevicesDisplayed?: boolean;
};

/**
 * Runs a BLE scan and list seen devices
 *
 * @param onDeviceSelect Function called when the user selects a scanned device
 * @param filterByDeviceModelId The only model of the devices that will be scanned
 * @param areKnownDevicesDisplayed Choose to display seen devices that are already known by LLM
 * @param onGoBack If this function is set, a back arrow is displayed that calls this function if pressed
 */
const BleDevicesScanning = ({
  onDeviceSelect,
  filterByDeviceModelId = null,
  areKnownDevicesDisplayed,
  onGoBack,
}: BleDevicesScanningProps) => {
  const { t } = useTranslation();
  const navigation = useNavigation();

  const productName = filterByDeviceModelId
    ? getDeviceModel(filterByDeviceModelId).productName || filterByDeviceModelId
    : null;

  const [locationDisabledError, setLocationDisabledError] =
    useState<boolean>(false);
  const [locationUnauthorizedError, setLocationUnauthorizedError] =
    useState<boolean>(false);

  // Nb Will reset when we regain focus to start scanning again.
  const [stopBleScanning, setStopBleScanning] = useResetOnNavigationFocusState(
    navigation,
    false,
  );

  const [isCantSeeDeviceShown, setIsCantSeeDeviceShown] =
    useState<boolean>(false);
  useEffect(() => {
    const cantSeeDeviceTimeout = setTimeout(
      () => setIsCantSeeDeviceShown(true),
      CANT_SEE_DEVICE_TIMEOUT,
    );

    return () => clearTimeout(cantSeeDeviceTimeout);
  }, []);

  const onWrappedDeviceSelect = useCallback(
    device => {
      setStopBleScanning(true);
      onDeviceSelect(device);
    },
    [onDeviceSelect, setStopBleScanning],
  );

  const onCantSeeDevicePress = useCallback(() => {
    track("button_clicked", {
      button: `Can't find ${productName ?? "device"} Bluetooth`,
    });
    Linking.openURL(urls.pairingIssues);
  }, [productName]);

  // If we want to filter on known devices:
  const knownDevices = useSelector(knownDevicesSelector);
  // .map creates a new array at each render and it was being used as a dependency on a useEffect
  // inside useBleDevicesScanning, so we need to memo it.
  const knownDeviceIds = useMemo(
    () => knownDevices.map(device => device.id),
    [knownDevices],
  );

  // if we directly use an empty array in the call of the hook, we get an infinite loop render
  // since at each render the array will have a new reference ([] !== [])
  const filterOutDevicesByDeviceIds = useMemo(
    () => (areKnownDevicesDisplayed ? [] : knownDeviceIds),
    [areKnownDevicesDisplayed, knownDeviceIds],
  );

  const filterByDeviceModelIds = useMemo(
    () => (filterByDeviceModelId ? [filterByDeviceModelId] : undefined),
    [filterByDeviceModelId],
  );

  const { scannedDevices, scanningBleError } = useBleDevicesScanning({
    bleTransportListen: TransportBLE.listen,
    stopBleScanning,
    filterByDeviceModelIds,
    filterOutDevicesByDeviceIds,
  });

  // Handles scanning error
  useEffect(() => {
    if (scanningBleError) {
      if (
        scanningBleError.type ===
        HwTransportErrorType.BleLocationServicesDisabled
      ) {
        setStopBleScanning(true);
        setLocationDisabledError(true);
      }

      if (
        scanningBleError.type === HwTransportErrorType.BleBluetoothUnauthorized
      ) {
        setStopBleScanning(true);
        setLocationUnauthorizedError(true);
      }
    }
  }, [scanningBleError]);

  const onLocationFixed = useCallback(() => {
    setLocationDisabledError(false);
    setLocationUnauthorizedError(false);
    setStopBleScanning(false);
  }, [setLocationDisabledError, setLocationUnauthorizedError]);

  if (locationDisabledError) {
    return <LocationRequired onRetry={onLocationFixed} errorType="disabled" />;
  }

  if (locationUnauthorizedError) {
    return (
      <LocationRequired onRetry={onLocationFixed} errorType="unauthorized" />
    );
  }

  return (
    <Flex flex={1}>
      <TrackScreen
        category={`Looking for ${productName ?? "device"} Bluetooth`}
      />
      {onGoBack && (
        <TouchableOpacity onPress={onGoBack}>
          <ArrowLeftMedium size={24} />
        </TouchableOpacity>
      )}
      <Flex flex={1} px={4}>
        <Flex height={180} alignItems="center" justifyContent="center">
          <Animation source={lottie} />
        </Flex>
        <Text
          mb={3}
          textAlign="center"
          variant="h4"
          fontWeight="semiBold"
          fontSize={24}
        >
          {productName
            ? t("blePairingFlow.scanning.withProductName.title", {
                productName,
              })
            : t("blePairingFlow.scanning.withoutProductName.title")}
        </Text>
        <Text
          mb={8}
          color="neutral.c70"
          textAlign="center"
          variant="body"
          fontWeight="medium"
        >
          {t("blePairingFlow.scanning.description")}
        </Text>

        <Flex flex={1}>
          <ScrollView>
            <Flex pb={10}>
              {scannedDevices
                .map(item => ({
                  deviceId: item.deviceId,
                  deviceName: `${item.deviceName}`,
                  wired: false,
                  modelId: item.deviceModel.id,
                  isAlreadyKnown: Boolean(
                    knownDeviceIds.some(deviceId => deviceId === item.deviceId),
                  ),
                }))
                // unknown devices go first, already known devices go last
                .sort((a, b) =>
                  a.isAlreadyKnown === b.isAlreadyKnown
                    ? 0
                    : a.isAlreadyKnown
                    ? 1
                    : -1,
                )
                .map(deviceMeta => (
                  <BleDeviceItem
                    onSelect={() => onWrappedDeviceSelect(deviceMeta)}
                    key={deviceMeta.deviceId}
                    deviceMeta={deviceMeta}
                  />
                ))}
            </Flex>
          </ScrollView>
        </Flex>
      </Flex>
      {productName !== null && isCantSeeDeviceShown && (
        <Text
          textAlign="center"
          mb={40}
          fontSize={14}
          fontWeight="semiBold"
          onPress={onCantSeeDevicePress}
        >
          {t("blePairingFlow.scanning.cantSeeDevice", { productName })}
        </Text>
      )}
    </Flex>
  );
};

export default BleDevicesScanning;

import React, { Component } from "react";
import {
  GestureResponderEvent,
  TouchableOpacity,
  TouchableOpacityProps,
} from "react-native";
// eslint-disable-next-line import/no-cycle
import { track } from "../analytics";

const defaultHitSlop = {
  // default & can be overridden by rest
  top: 16,
  left: 16,
  right: 16,
  bottom: 16,
};
export type Props = {
  // when on press returns a promise,
  // the button will toggle in a pending state and
  // will wait the promise to complete before enabling the button again
  // it also displays a spinner if it takes more than WAIT_TIME_BEFORE_SPINNER
  onPress?: (() => (Promise<unknown> | null | undefined) | void) | null;
  onLongPress?: ((event?: GestureResponderEvent) => void) | undefined;
  children: React.ReactNode;
  event?: string;
  eventProperties?: Record<string, unknown>;
  style?: TouchableOpacityProps["style"];
  touchableTestID?: string;
} & TouchableOpacityProps;
export default class Touchable extends Component<
  Props,
  {
    pending: boolean;
  }
> {
  state = {
    pending: false,
  };
  unmounted = false;

  componentWillUnmount() {
    this.unmounted = true;
  }

  onPress = async () => {
    const { onPress, event, eventProperties } = this.props;
    if (!onPress) return;

    try {
      if (event) {
        track(event, eventProperties);
      }

      const res = onPress();

      if (res && res instanceof Promise) {
        // it's a promise, we will use pending/spinnerOn state
        this.setState({
          pending: true,
        });
        await res;
      }
    } finally {
      if (!this.unmounted) {
        this.setState(({ pending }) =>
          pending
            ? {
                pending: false,
              }
            : null,
        );
      }
    }
  };
  onLongPress = async () => {
    const { onLongPress, event, eventProperties } = this.props;
    if (!onLongPress) return;

    if (event) {
      track(event, eventProperties);
    }

    const res = onLongPress();
    this.setState({
      pending: true,
    });
    await res;
    this.setState({
      pending: false,
    });
  };

  render() {
    const {
      onPress,
      children,
      event,
      eventProperties,
      touchableTestID,
      ...rest
    } = this.props;
    const { pending } = this.state;
    const disabled = !onPress || pending;
    return (
      <TouchableOpacity
        delayPressIn={50}
        onPress={this.onPress}
        disabled={disabled}
        hitSlop={defaultHitSlop}
        testID={touchableTestID ?? event}
        {...rest}
      >
        {children}
      </TouchableOpacity>
    );
  }
}

import React, {
  ReactNode,
  useCallback,
  useEffect,
  useRef,
  useState,
} from "react";
import { SafeAreaView } from "react-native";
import WebView, { WebViewMessageEvent } from "react-native-webview";
import NetInfo from "@react-native-community/netinfo";
