import React, { useCallback, useEffect, useState } from "react";
import {
  Animated,
  FlatList,
  Linking,
  TouchableHighlight,
  View,
  RefreshControl,
} from "react-native";

import { NotificationCard, Box, Flex, Text } from "@ledgerhq/native-ui";

