import { TrackScreen } from "../../../analytics";
import SeedWarning from "../shared/SeedWarning";
import Illustration from "../../../images/illustration/Illustration";

import StepLottieAnimation from "./setupDevice/scenes/StepLottieAnimation";
import { completeOnboarding } from "../../../actions/settings";
import { useNavigationInterceptor } from "../onboardingContext";
import useNotifications from "../../../logic/notifications";
import {
  RootComposite,
  StackNavigatorNavigation,
  StackNavigatorProps,
} from "../../../components/RootNavigator/types/helpers";
import { OnboardingNavigatorParamList } from "../../../components/RootNavigator/types/OnboardingNavigator";
import { BaseOnboardingNavigatorParamList } from "../../../components/RootNavigator/types/BaseOnboardingNavigator";
import { Step } from "./setupDevice/scenes/BaseStepperView";

const images = {
  light: {
    Intro: require("../../../images/illustration/Light/_076.png"),
  },
  dark: {
    Intro: require("../../../images/illustration/Dark/_076.png"),
  },
};

type Metadata = {
  id: string;
  illustration: JSX.Element | null;
  drawer: null | { route: string; screen: string };
};

type NavigationProps = RootComposite<
  StackNavigatorProps<
    OnboardingNavigatorParamList,
    ScreenName.OnboardingPairNew
  >
>;

const scenes = [PairNew, ConnectNano] as Step[];

function OnboardingStepPairNew() {
  const navigation = useNavigation<NavigationProps["navigation"]>();
  const { theme } = useTheme();
  const route = useRoute<NavigationProps["route"]>();

  const dispatch = useDispatch();
  const { triggerJustFinishedOnboardingNewDevicePushNotificationModal } =
    useNotifications();
  const { resetCurrentStep } = useNavigationInterceptor();

  const { deviceModelId, showSeedWarning, next, isProtectFlow } = route.params;

  const newDeviceSelectionFeatureFlag = useFeature("llmNewDeviceSelection");

  const metadata: Array<Metadata> = useMemo(
    () => [
      {
        id: PairNew.id,
        illustration: (
          <Illustration
            size={150}
            darkSource={images.dark.Intro}
            lightSource={images.light.Intro}
          />
        ),
        drawer: {
          route: ScreenName.OnboardingBluetoothInformation,
          screen: ScreenName.OnboardingBluetoothInformation,
        },
      },
      {
        id: ConnectNano.id,
        illustration: newDeviceSelectionFeatureFlag?.enabled ? null : (
          <StepLottieAnimation
            stepId="pinCode"
            deviceModelId={deviceModelId}
            theme={theme === "dark" ? "dark" : "light"}
          />
        ),
        drawer: isProtectFlow
          ? {
              route: ScreenName.OnboardingProtectionConnectionInformation,
              screen: ScreenName.OnboardingProtectionConnectionInformation,
            }
          : {
              route: ScreenName.OnboardingBluetoothInformation,
              screen: ScreenName.OnboardingBluetoothInformation,
            },
      },
    ],
    [deviceModelId, theme, newDeviceSelectionFeatureFlag?.enabled],
  );

  const startPostOnboarding = useStartPostOnboardingCallback();

  const onFinish = useCallback(() => {
    if (next) {
      // only used for protect for now
      navigation.navigate(next as ScreenName.OnboardingProtectFlow, {
        deviceModelId,
      });
      return;
    }
    dispatch(completeOnboarding());
    resetCurrentStep();

    const parentNav =
      navigation.getParent<
        StackNavigatorNavigation<
          BaseOnboardingNavigatorParamList,
          NavigatorName.Onboarding
        >
      >();
    if (parentNav) {
      parentNav.popToTop();
    }

    startPostOnboarding({
      deviceModelId: deviceModelId as DeviceModelId,
      resetNavigationStack: true,
      fallbackIfNoAction: () =>
        navigation.navigate(NavigatorName.Base, {
          screen: NavigatorName.Main,
        }),
    });

    triggerJustFinishedOnboardingNewDevicePushNotificationModal();
  }, [
    dispatch,
    resetCurrentStep,
    navigation,
    startPostOnboarding,
    deviceModelId,
    triggerJustFinishedOnboardingNewDevicePushNotificationModal,
    next,
  ]);

  const nextPage = useCallback(() => {
    onFinish();
  }, [onFinish]);

  return (
    <>
      <TrackScreen category="Onboarding" name="PairNew" />
      <BaseStepperView
        onNext={nextPage}
        steps={scenes}
        metadata={metadata}
        deviceModelId={deviceModelId}
      />
      {showSeedWarning ? <SeedWarning deviceModelId={deviceModelId} /> : null}
    </>
  );
}

export default memo(OnboardingStepPairNew);

import React, { useCallback } from "react";
import { TouchableOpacity } from "react-native";
import { Trans } from "react-i18next";
import styled from "styled-components/native";
import { Flex, Text, Button } from "@ledgerhq/native-ui";
import { track, TrackScreen, updateIdentify } from "../../analytics";
import useRatings from "../../logic/ratings";

const NotNowButton = styled(TouchableOpacity)`
  align-items: center;
  justify-content: center;
  margin-top: 24px;
`;

type Props = {
  closeModal: () => void;
  setStep: (step: string) => void;
};

const Init = ({ closeModal, setStep }: Props) => {
  const {
    ratingsHappyMoment,
    handleRatingsSetDateOfNextAllowedRequest,
    ratingsFeatureParams,
    handleSatisfied,
    handleInitNotNow,
  } = useRatings();

  const goToEnjoy = useCallback(() => {
    setStep("enjoy");
    handleSatisfied();
    track("button_clicked", {
      flow: "review",
      page: "review_step0",
      button: "satisfied",
      source: ratingsHappyMoment?.route_name,
      params: ratingsFeatureParams,
    });
    updateIdentify();
  }, [
    setStep,
    handleSatisfied,
    ratingsHappyMoment?.route_name,
    ratingsFeatureParams,
  ]);
  const goToDisappointed = useCallback(() => {
    setStep("disappointed");
    track("button_clicked", {
      flow: "review",
      page: "review_step0",
      button: "disappointed",
      source: ratingsHappyMoment?.route_name,
      params: ratingsFeatureParams,
    });
    handleRatingsSetDateOfNextAllowedRequest(
      ratingsFeatureParams?.conditions?.disappointed_delay,
      { satisfaction: "disappointed" },
    );
    updateIdentify();
  }, [
    setStep,
    ratingsHappyMoment?.route_name,
    ratingsFeatureParams,
    handleRatingsSetDateOfNextAllowedRequest,
  ]);
  const onNotNow = useCallback(() => {
    closeModal();
    track("button_clicked", {
      flow: "review",
      page: "review_step0",
      button: "notnow",
      source: ratingsHappyMoment?.route_name,
      params: ratingsFeatureParams,
    });
    handleInitNotNow();
  }, [
    closeModal,
    ratingsHappyMoment?.route_name,
    handleRatingsSetDateOfNextAllowedRequest,
    ratingsFeatureParams,
  ]);

  return (
    <Flex flex={1} alignItems="center" justifyContent="center" mt={3}>
      <TrackScreen
        category="Review"
        name="page_viewed"
        flow="review"
        page="review_step0"
        source={ratingsHappyMoment?.route_name}
        params={ratingsFeatureParams}
      />
      <Text
        variant="h4"
        fontWeight="semiBold"
        color="neutral.c100"
        textAlign="center"
        lineHeight="34.8px"
      >
        <Trans i18nKey="ratings.init.title" />
      </Text>
      <Text
        variant="body"
        fontWeight="medium"
        color="neutral.c70"
        textAlign="center"
        my={6}
        lineHeight="23.8px"
      >
        <Trans i18nKey="ratings.init.description" />
      </Text>
      <Flex alignSelf="stretch" py={6}>
        <Button onPress={goToEnjoy} outline type="shade" size="large">
          <Trans i18nKey="ratings.init.cta.enjoy" />
        </Button>
        <Button
          onPress={goToDisappointed}
          outline
          type="shade"
          mt={3}
          size="large"
        >
          <Trans i18nKey="ratings.init.cta.disappointed" />
        </Button>
        <NotNowButton onPress={onNotNow}>
          <Text variant="large" fontWeight="semiBold" color="neutral.c100">
            <Trans i18nKey="ratings.init.cta.notNow" />
          </Text>
        </NotNowButton>
      </Flex>
    </Flex>
  );
};

export default Init;
