export const Hints = ({
  action,
  color,
}: {
  action: "accept" | "left",
  color: string,
}) => (
  <>
    <Defs>
      <LinearGradient id="hints" x1="50%" x2="50%" y1="0%" y2="100%">
        <Stop offset="0%" stopColor={color} stopOpacity="0" />
        <Stop offset="100%" stopColor={color} />
      </LinearGradient>
    </Defs>
    {action === "accept" ? (
      <>
        <LeftHint />
        <RightHint />
      </>
    ) : action === "left" ? (
      <LeftHint />
    ) : null}
  </>
);

